import { Card, Box, Button, CardActions, CardContent, TextField, Typography, CardHeader } from '@mui/material'
import axios from 'axios';
import React, { useState } from 'react'
import { useCookies } from "react-cookie"
import { useUserStore } from '../../../stores';

export default function SignIn() {
    const [userEmail, setEmail] = useState<string>('');
    const [userPwd, setPassword] = useState<string>('');
    const [cookies, setCookies] = useCookies();

    const { user, setUser } = useUserStore();

    const signInHandler = () => {
        if (userEmail.length === 0 || userPwd.length === 0) {
            alert('이메일과 비밀번호를 입력하세요.')
            return;
        }

        const data = {
            userEmail,
            userPwd
        }
        axios
            .post("http://localhost:8080/api/auth/signIn", data)
            .then((response) => {
                const responseData = response.data;
                if (!responseData.result) {
                    console.log('로그인에 실패했습니다.')
                }
                const { token, exprTime, user } = responseData.data;
                const expires = new Date();
                expires.setMilliseconds(expires.getMilliseconds + exprTime);

                setCookies('token', token, { expires });
                setUser(user);
            })
            .catch((error) => {
                alert('로그인에 실패했습니다.')
            });
    };
    return (
        <><Card sx={{ minWidth: 275, maxWidth: "50vw", padding: 5 }}>
            <Box>
                <Typography>로그인</Typography>
            </Box>
                <Box height={'50vh'}>
                    <TextField
                        fullWidth
                        label="이메일"
                        type="email"
                        variant="standard"
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <TextField
                        fullWidth
                        label="비밀번호"
                        type="password"
                        variant="standard"
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </Box>

                <Box component='div'>
                    <Button fullWidth onClick={() => signInHandler()} variant="contained">
                        로그인
                    </Button>
                </Box>

                <Box component='div'>
                    <Typography>신규 사용자 이신가요?</Typography>

                </Box> 
        </Card></>
    )
}
